//Project Euler: Problem 8

#include <stdio.h>
#include <stdlib.h>

int main()
{
    char a [1001] = "";
    int b [1001];
    int i,j;
    long unsigned int product = 1;
    long unsigned int productstore = 0;
    
    for (i=0; i<1000; i++)
        switch (a[i]){
            case '0':
                b[i] = 0;
                break;
            case '1':
                b[i] = 1;
                break;
            case '2':
                b[i] = 2;
                break;
            case '3':
                b[i] = 3;
                break;
            case '4':
                b[i] = 4;
                break;
            case '5':
                b[i] = 5;
                break;
            case '6':
                b[i] = 6;
                break;
            case '7':
                b[i] = 7;
                break;
            case '8':
                b[i] = 8;
                break;
            case '9':
                b[i] = 9;
                break;
            default :
                printf ("Error: NaN.\n");
                break;
        }

    for (i=0; i<=1000; i++)
    {
        product = 1;
        for (j=i; j<=i+12; j++)
            product *= b[j];

        if (product >= productstore)
            productstore = product;
        
    }
    
    printf ("%lu", productstore);

    return 0;
    
}

